import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function Home() {
  const [urls, setUrls] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetchUrls();
  }, []);

  const fetchUrls = async () => {
    try {
      const res = await axios.get('http://localhost:5050/urls');
      setUrls(res.data);
    } catch (err) {
      console.error('Failed to fetch URLs:', err);
    }
  };

  const handleDelete = async (id) => {
    try {
        console.log('Current URLs:', urls);
      await axios.delete(`http://localhost:5050/urls/${id}`);
      setUrls(prev => prev.filter((item) => item._id !== id));
    } catch (err) {
      console.error('Failed to delete:', err);
    }
  };

  const goToAddUrl = () => {
    navigate('/add');
  };

  return (
    <div>
      <h2>üìö Read Later List</h2>
      <button onClick={goToAddUrl}>‚ûï Add URL</button>
      <ul>
        {urls.map((item) => (
          <li key={item._id}>
            {item.url}
            <button onClick={() => handleDelete(item._id)} style={{ marginLeft: '10px' }}>
              ‚ùå Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Home;
