import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';

function SummaryPage() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [urlData, setUrlData] = useState(null);
  const [editing, setEditing] = useState(false);
  const [editedSummary, setEditedSummary] = useState('');

  useEffect(() => {
    axios.get('http://localhost:5050/urls')
      .then(res => {
        const item = res.data.find(i => i._id === id);
        if (item) {
          setUrlData(item);
          setEditedSummary(item.summary);
        }
      });
  }, [id]);

  const handleSave = async () => {
    await axios.patch(`http://localhost:5050/urls/${id}`, { summary: editedSummary });
    setEditing(false);
    setUrlData(prev => ({ ...prev, summary: editedSummary }));
  };

  if (!urlData) return <div>Loading...</div>;

  return (
    <div>
      <h2>üìù Summary for: <a href={urlData.url} target="_blank" rel="noopener noreferrer">{urlData.url}</a></h2>
      {editing ? (
        <>
          <textarea
            value={editedSummary}
            onChange={(e) => setEditedSummary(e.target.value)}
            rows={12}
            cols={80}
          />
          <br />
          <button onClick={handleSave}>üíæ Save</button>
          <button onClick={() => setEditing(false)}>Cancel</button>
        </>
      ) : (
        <>
          <p>{urlData.summary}</p>
          <button onClick={() => setEditing(true)}>‚úèÔ∏è Edit Summary</button>
        </>
      )}
      <br /><br />
      <button onClick={() => navigate('/')}>‚¨Ö Back to Home</button>
    </div>
  );
}

export default SummaryPage;
